jQuery(document).ready(function($){$('.input_search').autocomplete({source:function(request,response){$.ajax({url:wp_ajax.ajaxurl,dataType:'json',data:{action:'search_posts',term:request.term,},success:function(data){response(data);$('.ui-autocomplete').removeClass('nodata')}})},minLength:1,select:function(event,ui){window.location.href=ui.item.link},response:function(event,ui){if(!ui.content.length){$('.ui-autocomplete').removeClass('dataadd');var noResult={value:"",label:"No results found"};ui.content.push(noResult)}else{$('.ui-autocomplete').addClass('dataadd')}}})})